package EX012_COMPARISONOPERATOR;

public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int	x	=	3;
		int	y	=	4;
		
		System.out.print((x == y)+" ");		//같다
		System.out.print((x != y)+" ");		//다르다
		System.out.print((x > y)+" ");		
		System.out.print((x < y)+" ");		
		System.out.print((x <= y)+" ");
	}

}
